package com.syntax.class08;

public class DoLoopMult {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		//I want to create a mult table
		/*   1*1=1
		 *   1*2=2
		 *   1*3=3
		 *   
		 *   to 10
		 */  
		
		int num1=1;
		int mult;
		for(int i=1; i<=10; i++) {
		mult=num1*i;
		System.out.println(num1+" X " + i + " = " + mult);
		}
	
		
		
		
		
		
	}

}
