package com.syntax.class08;

public class KeyWordContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//continue will skip 1 iteration
		//i want to print numbers from 1 to 5 exept number 3
		
		
		for(int i=1; i<=5; i++) {
			if(i==3) {
				continue;
			}
			System.out.print(i + " ");
		}
		
		
		
		
		
		
		
		
		
		
		
	}

}
