package com.syntax.class09;

public class Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub



		for(int r =1; r<=7; r++) {
			for(int c=1; c<=8; c++ ) {
				System.out.print(c);
			}
			System.out.println();
		}
		System.out.println("---------------------");
		
		for(int r =1; r<=5; r++) {
			for(int c=7; c>=1; c--) {
				System.out.print(c);
				}	
			}
			System.out.println();
		}
	

}
